import 'package:convert/convert.dart';
import 'package:fido2/src/ctap.dart';

class MockDevice extends CtapDevice {
  @override
  Future<CtapResponse<List<int>>> transceive(List<int> command) async {
    final cmd = hex.encode(command);
    switch (cmd) {
      case '04':
        return CtapResponse(
            0x00,
            hex.decode(
                ''));
    }
    return CtapResponse(0xFF, List.empty());
  }
}
